(53, 'Tr chi dn gian: Nu na nu nng', 'tro-chi-dan-gian-nu-na-nu-nng', '', '<p style="margin-left: 5px; text-align: justify;"><em><span style="font-family: Arial;"><strong><em><span style="font-family: Arial;">\r\n<table style="font-family: Arial; width: 489px; height: 162px;" border="0">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><em><span style="font-family: Arial;"><strong><em><span style="font-family: Arial;"><img style="float: left;" src="http://tbn1.google.com/images?q=tbn:o6xdnM4ptImFkM:%3Ca%20href=" height="166" width="226" /></span></em></strong></span></em></p>\r\n</td>\r\n<td>\r\n<p style="text-align: justify;"><span style="color: #ff6600; font-size: 8pt;"><em><span style="font-family: Arial;"><strong><em><span style="font-family: Arial;">Nu na nu nng<br />Ci cng nm trong<br />Ci ong nm ngoi<br />C khoai chm mt<br />Bt ngi bt khc<br /></span></em>Con cc nhy ra<br />ng gi  <br />B m thi xi<br />Nh ti nu ch<br />T he chn rt</strong></span></em></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</span></em></strong></span></em></p>\r\n<p style="margin-right: 10px;" align="justify"><span style="font-family: Arial; font-size: 8pt;">m tr ngi thnh hng ngang, dui hai chn ra trc. Mt a ngi i din, ly tay p vo tng bn <span class="IL_AD" id="IL_AD2">chan <span class="IL_AD" id="IL_AD3">theo nhp tng t mt ca bi ht trn. Dt bi, t "rt" ng vo chn em no th phi rt nhanh. Nu b tay ca ci p vo chn th em  thua cuc: ra lm ci vn chi k tip, hoc chu hnh pht (nhy l c mt vng, trng chui...) hay phi ng ra lm ci <span class="IL_AD" id="IL_AD4">cho mt tr chi khc (bt mt bt d,  tm, c su ln b...)</span></span></span></span></p>', '', 1, 25, 0, 134, '2011-11-24 07:05:29', 62, '', '2011-11-25 04:07:18', 62, 0, '0000-00-00 00:00:00', '2011-11-24 07:01:24', '0000-00-00 00:00:00', '', '', '', 5, 0, 3, '', '', 0, 12, ''),
(54, 'Tr chi dn gian: Ko co', 'tro-chi-dan-gian-keo-co', '', '<p>\r\n<table align="left" border="0">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://thanhhai.violet.vn/uploads/resources/blog/2439/keoco.jpg" height="167" width="250" border="0" /></td>\r\n<td><span style="font-size: 8pt;"></span></td>\r\n</tr>\r\n<tr>\r\n<td><span style="font-size: 8pt;"></span></td>\r\n<td><span style="font-size: 8pt;"></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<p style="margin-left: 15px; margin-right: 10px;" align="justify"><span style="font-family: Arial; font-size: 8pt;"><span style="font-family: Arial;">Tc ko co  mi ni c nhng li chi khc nhau, nhng bao gi s ngi chi cng <span class="IL_AD" id="IL_AD1">chia lm hai phe, mi phe cng dng sc mnh  ko <span class="IL_AD" id="IL_AD3">cho c bn <span class="IL_AD" id="IL_AD4">kia ng v pha mnh. C khi c hai bn u l nam, c khi bn nam, bn n. Trong trng hp bn nam bn n, dn lng thng chn nhng trai gi cha v cha chng.<br />Mt ct tr   gia sn chi, c dy thng buc di hay dy <span class="IL_AD" id="IL_AD5">song, dy tre hoc cy tre, thng di khong 20m cng u ra hai pha, hai bn xm nhau nm ly dy thng  ko. Mt v chc sc hay b lo cm trch ra hiu lnh. Hai bn ra sc ko, sao cho ct tr ko v bn mnh l thng. Bn ngoi dn lng c v hai bn bng ting "d ta", "c ln".</span></span></span></span></span></span></p>\r\n<p style="margin-left: 15px; margin-right: 10px;" align="justify"><span style="font-family: Arial; font-size: 8pt;">C ni ngi ta ly tay ngi, sc ngi trc tip ko co. Hai ngi ng u hai bn nm ly tay nhau, cn cc ngi sau m bng ngi trc m ko. ang gia cuc, mt ngi bn no b t dy l thua bn kia. Ko co cng ko ba keo, bn no thng lin ba keo l bn y c.</span></p>', '', 1, 25, 0, 134, '2011-11-24 07:12:13', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2011-11-24 07:11:12', '0000-00-00 00:00:00', '', '', '', 1, 0, 2, '', '', 0, 13, '');
